void compare_cells( unsigned char* packed_one, unsigned char* packed_two, int length ) {
  int i;

  // if the length isn't variable, just use the cell length
  if ( length == 0 ) {
    length = CELL_LEN;
  }

  for ( i = 0; i < length; i++ ) {
    if ( packed_one[i] != packed_two[i] ) {
      printf( "failed at %d\n", i );
      printf( "packed_one 0x%2x\n", packed_one[i] );
      printf( "packed_two 0x%2x\n", packed_two[i] );

      if ( i > 0 ) {
        printf( "packed_one previous 0x%2x\n", packed_one[i - 1] );
        printf( "packed_two previous 0x%2x\n", packed_two[i - 1] );
      }
    }

    assert( packed_one[i] == packed_two[i] );
  }
}

void fill_to_length( unsigned char* buffer, int length ) {
  int i;

  for ( i = 0; i < length; i++ ) {
    buffer[i] = (unsigned char)i;
  }
}

void fill_to_length_short( unsigned short* buffer, int length ) {
  int i;

  for ( i = 0; i < length; i++ ) {
    buffer[i] = (unsigned short)i;
  }
}

void compare_to_length( unsigned char* buffer, int length ) {
  int i;

  for ( i = 0; i < length; i++ ) {
    if ( buffer[i] != i ) {
      printf( "buffer 0x%2x\n", buffer[i] );
      printf( "expected 0x%2x\n", i );
    }

    assert( buffer[i] == i );
  }
}

void compare_to_length_short( unsigned short* buffer, int length ) {
  int i;

  for ( i = 0; i < length; i++ ) {
    if ( buffer[i] != i ) {
      printf( "buffer 0x%2x\n", buffer[i] );
      printf( "expected 0x%2x\n", i );
    }

    assert( buffer[i] == i );
  }
}
